    @Override
    public PsDepositAccount queryAccount(DomainBaseCEnum.E_CURRENT_FIXED_FLAG demandOrTimeFlag, String fundAcctNo) {
        PsDepositAccount psDepositAccount = null;
        //查询活期账户信息
        if (E_CURRENT_FIXED_FLAG.CURRENT.equals(demandOrTimeFlag) || CommUtil.isNull(demandOrTimeFlag)) {
            KcAcctCurtPo kcAcctCurtPo = KcAcctCurtPoDao.selectOne_odb1(fundAcctNo, false);
            if (CommUtil.isNotNull(kcAcctCurtPo)) {

                PsDepositAccountStatus psDepositAccountStatus = new PsDepositAccountStatus();
                BizCommonUtil.copyProperties(psDepositAccountStatus, kcAcctCurtPo);

                PsDepositAccountBalance psDepositAccountBalance = new PsDepositAccountBalance();
                BizCommonUtil.copyProperties(psDepositAccountBalance, kcAcctCurtPo);

                PsDepositAccountWaitDrawInrt psDepositAccountWaitDrawInrt = new PsDepositAccountWaitDrawInrt();
                BizCommonUtil.copyProperties(psDepositAccountWaitDrawInrt, kcAcctCurtPo);
                psDepositAccountBalance.setPsDepositAccountWaitDrawInrt(psDepositAccountWaitDrawInrt);

                if (DomainBaseAEnum.E_ACCT_CLASS.ELEC_CASH_ACCT == kcAcctCurtPo.getAcctClass()) {
                    ElectronicCashAccount e = new ElectronicCashAccount();
                    BizCommonUtil.copyProperties(e, kcAcctCurtPo);
                    e.setPsDepositAccountBalance(psDepositAccountBalance);
                    e.setPsDepositAccountStatus(psDepositAccountStatus);
                    e.setSpareBal(kcAcctCurtPo.getSpareAmt());
                    psDepositAccount = e;
                    psDepositAccount.setDemandOrTimeFlag(E_CURRENT_FIXED_FLAG.CURRENT);
                } else {
                    PsCurrentAccount psCurrentAccount = new PsCurrentAccount();
                    psCurrentAccount.setDemandOrTimeFlag(E_CURRENT_FIXED_FLAG.CURRENT);
                    BizCommonUtil.copyProperties(psCurrentAccount, kcAcctCurtPo);
                    psCurrentAccount.setPsDepositAccountBalance(psDepositAccountBalance);
                    psCurrentAccount.setPsDepositAccountStatus(psDepositAccountStatus);

                    if (CLASS2_ACCT_302.equals(kcAcctCurtPo.getAcctAttr()) ||
                            CLASS3_ACCT_303.equals(kcAcctCurtPo.getAcctAttr())) {
                        //查询二三类户绑定I类账户登记簿
                        List<KbAcctBindClassiPo> acctBindClassiPoList = KbAcctBindClassiPoDao.selectAll_odb2(fundAcctNo, false);
                        List<BindClassIAccount> bindClassIAccountList = new ArrayList<>();
                        for (KbAcctBindClassiPo po : acctBindClassiPoList) {
                            if (po.getEffectFlag() == DomainBaseWEnum.E_WHETHER_FLAG.YES) {
                                BindClassIAccount account = new BindClassIAccount();
                                BizCommonUtil.copyProperties(account, po);
                                bindClassIAccountList.add(account);
                            }
                        }
                        psCurrentAccount.setBindClass1AccountList(bindClassIAccountList);

                    }

                    psDepositAccount = psCurrentAccount;

                }
            }
        }
        //查询定期账户信息
        if ((E_CURRENT_FIXED_FLAG.FIXED.equals(demandOrTimeFlag) || CommUtil.isNull(demandOrTimeFlag)) && psDepositAccount == null) {
            KcAcctFixbPo kcAcctFixbPo = KcAcctFixbPoDao.selectOne_odb1(fundAcctNo, false);
            if (CommUtil.isNotNull(kcAcctFixbPo)) {
                PsDepositAccountStatus psDepositAccountStatus = new PsDepositAccountStatus();
                BizCommonUtil.copyProperties(psDepositAccountStatus, kcAcctFixbPo);
                PsDepositAccountBalance psDepositAccountBalance = new PsDepositAccountBalance();
                BizCommonUtil.copyProperties(psDepositAccountBalance, kcAcctFixbPo);
                PsDepositAccountWaitDrawInrt psDepositAccountWaitDrawInrt = new PsDepositAccountWaitDrawInrt();
                BizCommonUtil.copyProperties(psDepositAccountWaitDrawInrt, kcAcctFixbPo);
                psDepositAccountBalance.setPsDepositAccountWaitDrawInrt(psDepositAccountWaitDrawInrt);

                switch (kcAcctFixbPo.getAcctClass()) {
                    case FIXED_DEPOSIT_ACCT:
                        LumpSumDepositAccount lumpSumDepositAccount = new LumpSumDepositAccount();
                        BizCommonUtil.copyProperties(lumpSumDepositAccount, kcAcctFixbPo);
                        psDepositAccount = lumpSumDepositAccount;
                        break;
                    case INSTALLMENT_FIXED_DEPOSIT_ACCT:
                        IndiviZeroDpwithDrawAccount indiviZeroDpwithDrawAccount = new IndiviZeroDpwithDrawAccount();
                        BizCommonUtil.copyProperties(indiviZeroDpwithDrawAccount, kcAcctFixbPo);
                        psDepositAccount = indiviZeroDpwithDrawAccount;
                        break;
                    case FIXED_DEP_WITH_PARTIAL_WDL_ACCT:
                        WholeDpZeroDrAccount wholeDpZeroDrAccount = new WholeDpZeroDrAccount();
                        BizCommonUtil.copyProperties(wholeDpZeroDrAccount, kcAcctFixbPo);
                        psDepositAccount = wholeDpZeroDrAccount;
                        break;
                    case FIXED_DEP_WITH_INRT_WDL_ACCT:
                        DepositPlDrawIntAccount depositAccount = new DepositPlDrawIntAccount();
                        BizCommonUtil.copyProperties(depositAccount, kcAcctFixbPo);
                        psDepositAccount = depositAccount;
                        break;
                    case NOTICE_DEPOSIT_ACCT:
                        CallDepositAccount callDepositAccount = new CallDepositAccount();
                        BizCommonUtil.copyProperties(callDepositAccount, kcAcctFixbPo);
                        psDepositAccount = callDepositAccount;
                        break;
                    default:
                        psDepositAccount = new LumpSumDepositAccount();//默认为整存
                        break;
                }

                psDepositAccount.setPsDepositAccountStatus(psDepositAccountStatus);
                psDepositAccount.setPsDepositAccountBalance(psDepositAccountBalance);
                psDepositAccount.setDemandOrTimeFlag(E_CURRENT_FIXED_FLAG.FIXED);
            }
        }

        if (CommUtil.isNull(psDepositAccount)) {
            return null;
        }

        List<PsDepositControl> psDepositControlList = new ArrayList<>();

        List<KbAcctRstrctPo> kbAcctRstrctPoList = AccountBusiTables.KbAcctRstrctPoDao.selectAll_odb2(fundAcctNo, DomainBaseCEnum.E_CONTROL_DECONTROL_FLAG.CONTROL, false);
        if (CommUtil.isNotNull(kbAcctRstrctPoList)) {
            for (KbAcctRstrctPo po : kbAcctRstrctPoList) {
                ControlEnforcement controlEnforcement = new ControlEnforcement();
                BizCommonUtil.copyProperties(controlEnforcement, po);
                if (po.getFreezeSrc() == DomainBaseFEnum.E_FREEZE_SOURCE.FREEZE){
                    PsDepositFroz psDepositFroz = new PsDepositFroz();
                    psDepositFroz.setFreezeQueueFlag(DomainBaseWEnum.E_WHETHER_FLAG.NO);
                    BizCommonUtil.copyProperties(psDepositFroz, po);
                    psDepositFroz.setLawEnforceInfo(controlEnforcement);
                    psDepositControlList.add(psDepositFroz);
                } else{
                    PsDepositStopPay psDepositStopPay = new PsDepositStopPay();
                    BizCommonUtil.copyProperties(psDepositStopPay, po);
                    psDepositStopPay.setLawEnforceInfo(controlEnforcement);
                    psDepositControlList.add(psDepositStopPay);
                }
            }
        }
        //轮候冻结
        List<KbAcctFreezeQueuePo> kbAcctFreezeQueuePoList = KbAcctFreezeQueuePoDao.selectAll_odb4(fundAcctNo, DomainBaseWEnum.E_WHETHER_FLAG.YES, false);
        if (CommUtil.isNotNull(kbAcctFreezeQueuePoList)){
            for (KbAcctFreezeQueuePo freezeQueuePo : kbAcctFreezeQueuePoList) {
                PsDepositFrozQueue psDepositFrozQueue = new PsDepositFrozQueue();
                psDepositFrozQueue.setFreezeQueueFlag(DomainBaseWEnum.E_WHETHER_FLAG.YES);
                ControlEnforcement controlEnforcement = new ControlEnforcement();
                BizCommonUtil.copyProperties(controlEnforcement, freezeQueuePo);
                psDepositFrozQueue.setLawEnforceInfo(controlEnforcement);
                BizCommonUtil.copyProperties(psDepositFrozQueue, freezeQueuePo);
                psDepositControlList.add(psDepositFrozQueue);
            }
        }

        //账户控制措施
        PsDepositControlCompose psDepositControlCompose = new PsDepositControlCompose(psDepositControlList);
        psDepositControlCompose.setAcctControlList(psDepositControlList);
        psDepositAccount.setPsDepositControlCompose(psDepositControlCompose);

        //账户利息定义
        List<AccountInterest> accountInterestList = queryAccountInterestList(fundAcctNo);
        psDepositAccount.setAccountInterest(accountInterestList);

        //账户额度类余额
        AccountLimitBalance limitBalance = queryAcctLmtBal(fundAcctNo, null);
        psDepositAccount.setAccountLimitBalance(limitBalance);

        return psDepositAccount;
    }

