    public void checkAdjustFrozQueueNo(final cn.xxx.ts.account.aps.pojo.AccountApsDto.AdjustFrozQueueNoApsIn adjustFrozQueueNoApsIn){
        PsDepositAccount psDepositAccount = psDepositAccountRepo.queryAccount(null, adjustFrozQueueNoApsIn.getFundAcctNo());
        if (CommUtil.isNull(psDepositAccount)){
            throw AccountCommonError.AccountCommon.EACTB001(adjustFrozQueueNoApsIn.getFundAcctNo());
        }
        AccountDmsDto.AdjustFrozQueueNoIn adjustFrozQueueNoIn = SysUtil.getInstance(AccountDmsDto.AdjustFrozQueueNoIn.class);
        BizCommonUtil.copyProperties(adjustFrozQueueNoIn, adjustFrozQueueNoApsIn);
        if (CommUtil.isNotNull(adjustFrozQueueNoApsIn.getLawEnforcementInfo())){
            BizCommonUtil.copyProperties(adjustFrozQueueNoIn, adjustFrozQueueNoApsIn.getLawEnforcementInfo());
        }
        //查询所有轮候冻结数据
        List<PsDepositFrozQueue> frozQueueAllList = psDepositAccountRepo.queryFreezeQueueAllList(adjustFrozQueueNoApsIn.getFundAcctNo());
        List<PsDepositFrozQueue> frozQueueList = frozQueueAllList.stream().filter(e -> adjustFrozQueueNoApsIn.getFreezeSrc() == e.getFreezeSrc()).collect(Collectors.toList());
        psDepositAccount.checkAdjustFreezeQueueSerialNo(adjustFrozQueueNoIn, frozQueueList);
    }
